import React, { useState } from "react";

const AdminDashboard = () => {
  const [pendingItems, setPendingItems] = useState([]); // List of pending items
  const [approvedItems, setApprovedItems] = useState([]); // List of approved items
  const [auctions, setAuctions] = useState([]); // List of all auctions
  const [filterStatus, setFilterStatus] = useState("all"); // Current filter status

  const [formData, setFormData] = useState({
    auctionDate: "",
    startTime: "",
    endTime: "",
  }); // Form data for new auction

  // Handle input changes in the form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Create a new auction
  const createAuction = (e) => {
    e.preventDefault();

    if (!formData.auctionDate || !formData.startTime || !formData.endTime) {
      alert("Please fill in all fields.");
      return;
    }

    const newAuction = {
      id: Math.random().toString(36).substr(2, 9), // Unique ID
      name: `Auction - ${formData.auctionDate}`,
      date: formData.auctionDate,
      startTime: formData.startTime,
      endTime: formData.endTime,
      status: "upcoming",
    };

    setAuctions([...auctions, newAuction]);
    setFormData({ auctionDate: "", startTime: "", endTime: "" }); // Reset form fields
  };

  // Filter auctions based on status
  const filterAuctions = (status) => {
    setFilterStatus(status);
  };

  const filteredAuctions =
    filterStatus === "all"
      ? auctions
      : auctions.filter((auction) => auction.status === filterStatus);

  return (
    <div className="container mx-auto p-6 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-extrabold text-gray-800 mb-8 text-center">
        Admin Dashboard
      </h1>

      {/* System Overview Section */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
        <div className="bg-white shadow-lg p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-gray-700">Total Users</h3>
          <p className="text-2xl font-bold text-blue-600">100</p>
        </div>
        <div className="bg-white shadow-lg p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-gray-700">Active Auctions</h3>
          <p className="text-2xl font-bold text-green-600">5</p>
        </div>
        <div className="bg-white shadow-lg p-6 rounded-lg">
          <h3 className="text-lg font-semibold text-gray-700">Total Bids</h3>
          <p className="text-2xl font-bold text-purple-600">350</p>
        </div>
      </div>

      {/* Create Auction Section */}
      <div className="bg-white shadow-lg p-6 rounded-lg mb-12">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6">Create Auction</h2>
        <form onSubmit={createAuction}>
          <div className="mb-4">
            <label className="block font-medium text-gray-600 mb-1">
              Date of Auction
            </label>
            <input
              type="date"
              name="auctionDate"
              value={formData.auctionDate}
              onChange={handleInputChange}
              className="border border-gray-300 p-2 w-full rounded"
            />
          </div>
          <div className="mb-4">
            <label className="block font-medium text-gray-600 mb-1">Start Time</label>
            <input
              type="time"
              name="startTime"
              value={formData.startTime}
              onChange={handleInputChange}
              className="border border-gray-300 p-2 w-full rounded"
            />
          </div>
          <div className="mb-4">
            <label className="block font-medium text-gray-600 mb-1">End Time</label>
            <input
              type="time"
              name="endTime"
              value={formData.endTime}
              onChange={handleInputChange}
              className="border border-gray-300 p-2 w-full rounded"
            />
          </div>
          <button
            type="submit"
            className="bg-blue-600 text-white p-3 w-full rounded hover:bg-blue-700"
          >
            Create Auction
          </button>
        </form>
      </div>

      {/* Auction Filter Section */}
      <div className="bg-white shadow-lg p-6 rounded-lg mb-12">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6">Filter Auctions</h2>
        <div className="flex space-x-4">
          {["all", "upcoming", "ongoing", "closed"].map((status) => (
            <button
              key={status}
              onClick={() => filterAuctions(status)}
              className={`px-4 py-2 rounded ${
                filterStatus === status
                  ? "bg-blue-600 text-white"
                  : "bg-gray-200 text-gray-700"
              }`}
            >
              {status.charAt(0).toUpperCase() + status.slice(1)}
            </button>
          ))}
        </div>
      </div>

      {/* Display Filtered Auctions */}
      <div className="bg-white shadow-lg p-6 rounded-lg">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6">Filtered Auctions</h2>
        {filteredAuctions.length === 0 ? (
          <p className="text-gray-600">No auctions found.</p>
        ) : (
          <ul className="divide-y divide-gray-200">
            {filteredAuctions.map((auction) => (
              <li key={auction.id} className="py-4">
                <h4 className="text-lg font-bold text-gray-700">{auction.name}</h4>
                <p className="text-gray-600">Status: {auction.status}</p>
                <p className="text-gray-600">Date: {auction.date}</p>
                <p className="text-gray-600">
                  Time: {auction.startTime} - {auction.endTime}
                </p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
