import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import BASE_URL from "../components/api/config";

const Authentication = ({ setUserRole }) => {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("client");
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (event) => {
    event.preventDefault();
    setError("");

    try {
      const response = await fetch(`${BASE_URL}/auth/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      if (!response.ok) {
        const { message } = await response.json();
        setError(message || "Invalid credentials.");
        return;
      }
      const data = await response.json();
      localStorage.setItem("access_token", data.token);
      setUserRole(data.role);
      navigate(`/${data.role}-dashboard`);
    } catch (error) {
      setError("An error occurred during login. Please try again.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${BASE_URL}/users`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          username,
          email,
          password_hash: password,
          role,
        }),
      });

      if (!response.ok) {
        const { message } = await response.json();
        setError(message || "Registration failed.");
        return;
      }

      setIsLoginMode(true); // Switch to login mode after registration
    } catch (error) {
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gray-50">
      <div className="bg-white p-6 rounded shadow-md w-80">
        <h2 className="text-xl font-bold mb-4">{isLoginMode ? "Login" : "Register"}</h2>
        {error && <p className="text-red-500">{error}</p>}
        <form onSubmit={isLoginMode ? handleLogin : handleRegister}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full p-2 border mb-4"
            required
          />
          {!isLoginMode && (
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-2 border mb-4"
              required
            />
          )}
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-2 border mb-4"
            required
          />
          {!isLoginMode && (
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="w-full p-2 border mb-4"
            >
              <option value="client">Client</option>
              <option value="auctioneer">Auctioneer</option>
              <option value="admin">Admin</option>
            </select>
          )}
          <button className="w-full bg-blue-600 text-white py-2 rounded">
            {isLoginMode ? "Login" : "Register"}
          </button>
        </form>
        <button
          onClick={() => setIsLoginMode(!isLoginMode)}
          className="text-blue-500 mt-4 w-full text-center"
        >
          {isLoginMode ? "Don't have an account? Register" : "Already have an account? Login"}
        </button>
      </div>
    </div>
  );
};

export default Authentication;
