import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import BASE_URL from "./api/config.js";

const Authentication = ({ setUserRole }) => {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("client");
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false); // Loading state
  const navigate = useNavigate();

  // Handle Login
  const handleLogin = async (event) => {
    event.preventDefault();
    setError("");
    setLoading(true);

    try {
      const response = await fetch(`${BASE_URL}/auth/login`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          username,
          password,
        }),
      });

      if (!response.ok) {
        const { message } = await response.json();
        setError(message || "Login failed.");
        setLoading(false);
        return;
      }

      const data = await response.json();
      localStorage.setItem("access_token", data.token); // Store token
      localStorage.setItem("user_role", data.role); // Store user role
      setUserRole(data.role); // Update state with user role
      navigate(`/${data.role}-dashboard`); // Redirect based on role
      setLoading(false);
    } catch (error) {
      setError("An error occurred during login. Please try again.");
      setLoading(false);
    }
  };

  // Handle Register
  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const response = await fetch(`${BASE_URL}/auth/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          username,
          email,
          password,
          role,
        }),
      });

      if (!response.ok) {
        const { message } = await response.json();
        setError(message || "Registration failed.");
        setLoading(false);
        return;
      }

      const data = await response.json();
      setIsLoginMode(true); // Switch to login mode after registration
      setLoading(false);
    } catch (error) {
      setError("An error occurred during registration. Please try again.");
      setLoading(false);
    }
  };

  return (
    <div className="authentication">
      <h2>{isLoginMode ? "Login" : "Register"}</h2>
      {error && <p className="error">{error}</p>}
      <form
        onSubmit={isLoginMode ? handleLogin : handleRegister}
      >
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        {!isLoginMode && (
          <>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="client">Client</option>
              <option value="auctioneer">Auctioneer</option>
              <option value="admin">Admin</option>
            </select>
          </>
        )}
        <button type="submit" disabled={loading}>
          {loading ? "Processing..." : isLoginMode ? "Login" : "Register"}
        </button>
      </form>
      <p>
        {isLoginMode
          ? "Don't have an account? "
          : "Already have an account? "}
        <span onClick={() => setIsLoginMode(!isLoginMode)}>
          {isLoginMode ? "Register" : "Login"}
        </span>
      </p>
    </div>
  );
};

export default Authentication;
