import React, { useState, useEffect } from "react";
import BASE_URL from "../components";

const AdminDashboard = () => {
  const [pendingItems, setPendingItems] = useState([]);

  useEffect(() => {
    const fetchPendingItems = async () => {
      try {
        const response = await fetch(`${BASE_URL}/items`);
        if (!response.ok) throw new Error("Failed to fetch items");

        const data = await response.json();
        setPendingItems(data.filter((item) => item.status === "pending"));
      } catch (error) {
        console.error(error.message);
      }
    };
    fetchPendingItems();
  }, []);

  return (
    <div className="container mx-auto p-6 bg-gray-100">
      <h1 className="text-3xl font-semibold">Admin Dashboard</h1>
      <h2 className="text-2xl font-medium mt-6">Pending Auctions</h2>
      <div className="mt-4">
        {pendingItems.length === 0 ? (
          <p>No pending auctions.</p>
        ) : (
          <ul>
            {pendingItems.map((item) => (
              <li key={item.item_id} className="border p-4 mb-4">
                <h4 className="font-bold">{item.title}</h4>
                <p>{item.description}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
