import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import GoogleLogin from "./GoogleLogin";
import Navbar from "./Navbar";
import auctionBackground from "../images/auction1.jpg";
import BASE_URL from "..";

const Authentication = ({ setUserRole, userRole }) => {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [selectedRole, setSelectedRole] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("client");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  const handleRoleChange = (event) => {
    setSelectedRole(event.target.value);
  };

  const handleLogin = async (event) => {
    event.preventDefault();
    setError("");

    try {
      const response = await fetch(`${BASE_URL}/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        const { message } = await response.json();
        setError(message || "Invalid credentials.");
        return;
      }

      const data = await response.json();
      localStorage.setItem("access_token", data.token);
      setUserRole(data.role);
      navigate(`/${data.role}-dashboard`);
    } catch (error) {
      setError("An error occurred during login. Please try again.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccessMessage("");

    try {
      const response = await fetch(`${BASE_URL}/user`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          username,
          email,
          password,
          role,
        }),
      });

      if (!response.ok) {
        const { message } = await response.json();
        setError(message || "Registration failed.");
        setLoading(false);
        return;
      }

      setSuccessMessage("Registration successful! Redirecting to login...");
      setTimeout(() => setIsLoginMode(true), 2000);
    } catch (error) {
      setError("An error occurred. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="relative min-h-screen min-w-full bg-cover bg-center transition-opacity duration-300 hover:bg-opacity-75"
      style={{
        backgroundImage: `url(${auctionBackground})`,
        backgroundSize: "cover",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        height: "100vh",
        width: "100vw",
      }}
    >
      {userRole && <Navbar userRole={userRole} handleLogout={() => setUserRole(null)} />}

      <div className="flex items-center justify-center h-full">
        <div className="max-w-md p-6 bg-white rounded shadow-lg relative z-10 group hover:bg-opacity-75">
          <button
            onClick={() => navigate("/")}
            className="absolute top-4 left-4 bg-gray-500 text-white p-2 rounded"
          >
            Return Home
          </button>

          {isLoginMode ? (
            <>
              <h2 className="text-center text-2xl font-semibold mb-4">Login</h2>
              {error && <div className="text-red-500 text-sm mb-4">{error}</div>}
              <form onSubmit={handleLogin} className="space-y-4">
                <div className="mb-4">
                  <label className="block text-gray-700">Login as</label>
                  <select
                    value={selectedRole}
                    onChange={handleRoleChange}
                    className="w-full p-2 border border-gray-300 rounded"
                    required
                  >
                    <option value="">Select Role</option>
                    <option value="admin">Admin</option>
                    <option value="auctioneer">Auctioneer</option>
                    <option value="client">Client</option>
                  </select>
                </div>
                <input
                  type="text"
                  placeholder="Username"
                  className="w-full p-2 border border-gray-300 rounded"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
                <input
                  type="password"
                  placeholder="Password"
                  className="w-full p-2 border border-gray-300 rounded"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded">
                  Login
                </button>
              </form>
              <div className="my-4 text-center">or continue with:</div>
              <div className="mt-4">
                <GoogleLogin setUserRole={setUserRole} />
              </div>
              <div className="mt-4 text-center">
                <button onClick={() => setIsLoginMode(false)} className="text-blue-500">
                  Don’t have an account? Register
                </button>
              </div>
            </>
          ) : (
            <>
              <h2 className="text-2xl font-bold mb-4">Create an Account</h2>
              {error && <div className="text-red-500 text-sm mb-4">{error}</div>}
              {successMessage && <div className="text-green-500 text-sm mb-4">{successMessage}</div>}
              <form onSubmit={handleRegister} className="space-y-4">
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Username"
                  className="w-full p-2 border border-gray-300 rounded"
                  required
                />
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Email"
                  className="w-full p-2 border border-gray-300 rounded"
                  required
                />
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Password"
                  className="w-full p-2 border border-gray-300 rounded"
                  required
                />
                <select
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                  className="w-full p-2 border border-gray-300 rounded"
                >
                  <option value="client">Client</option>
                  <option value="auctioneer">Auctioneer</option>
                  <option value="admin">Admin</option>
                </select>
                <button
                  type="submit"
                  className="w-full bg-blue-500 text-white p-2 rounded"
                  disabled={loading}
                >
                  {loading ? "Registering..." : "Register"}
                </button>
              </form>
              <div className="mt-4 text-center">
                <button onClick={() => setIsLoginMode(true)} className="text-blue-500">
                  Already have an account? Login
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Authentication;
